import * as React from 'react';
import {
  MenuItem,
  FormControl,
  Select,
  Card,
  CardContent
} from "@material-ui/core";
import InfomationBox from './InformationBox';
import Map from './Map';
import Table from './Table';
import {sortData, prettyPrintStat} from "./helper";
import LineGraph from "./LineGraph"
import numeral from "numeral"
import './App.css';

const App = () => {
  const [countries, setCountries] = React.useState([]);
  const [country, setCountry] = React.useState(['WW']);
  const [countryInfo, setCountryInfo] = React.useState({});
  const [caseTable, setCaseTable] = React.useState([]);
  const [casesType, setCasesType] = React.useState("cases");
  const [mapCenter, setMapCenter] = React.useState({lat: 35, lng: -41})
  const [mapZoom, setMapZoom] = React.useState(3);


  React.useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/all")
    .then((response) => response.json())
    .then((data) => {
      setCountryInfo(data);
    });
  }, []);

  React.useEffect(() => {
    //run once as componet loads and not again
    // async -> send requent and wait for it then do something with it
    const getData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => {
        // it give back a big json
        const countries = data.map((country) => (
          { 
            // go through each country
            name : country.country, 
            value: country.countryInfo.iso3
          }
        ));
        const sorted = sortData(data);
        setCaseTable(sorted);
        setCountries(countries);
        setMapCenter([data.countryInfo.lat, data.countryInfo.long]);
        setMapZoom(5);
      });
    };
    getData();
  }, []);

  const onDropChange = async (event) => {
    const countryCode = event.target.value;
    // setCountry(countryCode);
    // get info for ww
    // get info for country

    const url = (countryCode === "WW" ? 'https://disease.sh/v3/covid-19/all' :
    `https://disease.sh/v3/covid-19/countries/${countryCode}`
    )
    await fetch(url)
    .then((response) => response.json())
    .then((data) => {
      setCountry(countryCode);
      setCountryInfo(data);

    });
  };
  return (
    <div className="app"> 
    <div className="app_left">
      <div className="app_header">
        <h1>Covid-19 Live tracing</h1>

        <FormControl className = "app_dropdown">
          <Select variant = "outlined" onChange={onDropChange} value={country}>
            <MenuItem value = "WW">WorldWide</MenuItem>
            {/* loop through all country and show drop down*/}
            {countries.map((country) => (
              <MenuItem value = {country.value}>{country.name}</MenuItem>
              ))}
          </Select>
        </FormControl>
      </div>

      <div className="app_stat">
        <InfomationBox onClick={(e) => setCasesType("cases")} title = "Covid-19 Cases" isRed active = {casesType === "cases"} cases = {prettyPrintStat(countryInfo.todayCases)} total = {numeral(countryInfo.cases).format("0.0a")}>
        </InfomationBox>
        <InfomationBox onClick={(e) => setCasesType("recovered")} title = "Recovered Cases" active = {casesType === "recovered"}  cases = {prettyPrintStat(countryInfo.todayRecovered)} total = {numeral(countryInfo.recovered).format("0.0a")}>
        </InfomationBox>
        <InfomationBox onClick={(e) => setCasesType("deaths")} title = "Death Cases" isRed active = {casesType === "deaths"} cases = {prettyPrintStat(countryInfo.todayDeaths)} total = {numeral(countryInfo.deaths).format("0.0a")}>
        </InfomationBox>
      </div>       
      {/*map*/}
      <Map center={mapCenter} zoom ={mapZoom}></Map>
    </div>

     <Card className="app_right">
       <CardContent>
         <div className = "app_tableAndChart">
          <h3><strong>Total Cases by Country</strong></h3>
          <Table countries = {caseTable}></Table>
            {/*table*/}
          <h3>Worldwide new {casesType}</h3>
            {/*graph*/}
            <LineGraph casesType={casesType} />
         </div>

       </CardContent>
     </Card>




    </div>
  );
};
export default App;
