import * as React from 'react';
import { Line } from "react-chartjs-2"

function LineGraph() {
    const[data, setData] = React.useState({});

    const buildChart = (data, casesType='cases') => {
      const chartData = [];
      //find the new cases by substracting the difference
      let lastData;
      for(let date in data.cases){
        if(lastData) {
          const newData = {
            x: date,
            y: data[casesType][date] - lastData
          };
          chartData.push(newData);
        }
        lastData = data['cases'][date];
      }
      return chartData;
    };

    React.useEffect(() => {
      fetch("https://disease.sh/v3/covid-19/historical/all?lastdays=120")
      .then((response) => response.json())
      .then((data) => {
        // get data for plotting line graph
        const chartData = buildChart(data);
        setData(chartData);
      });
    }, []);

    return (
        <div>
            <h1>This is graph</h1>
            <Line data = {{
              datasets: [{
                blackgroundColor: 'black',
                borderColor: 'black',
                data: data,
              },
              ]
            }}/>            
        </div>
    )
}

export default LineGraph
