import * as React from 'react';
import {
  MenuItem,
  FormControl,
  Select
} from "@material-ui/core";
import './App.css';

function App() {
  const [countries, setCountries] = React.useState([]);
  const [country, setCountry] = React.useState(['WW']);

  React.useEffect(() => {
    //run once as componet loads and not again
    // async -> send requent and wait for it then do something with it
    const getData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => {
        // it give back a big json
        const countries = data.map((country) => (
          { 
            // go through each country
            name : country.country, 
            value: country.countryInfo.iso3
          }
        ));
        setCountries(countries);
      });
    };
    getData();
  }, [countries]);

  const onDropChange = async (event) => {
    const countryCode = event.target.value;
    setCountry(countryCode);
  }
  return (
    <div className="app"> 
    <div className="app__header">
      <h1>Covid-19 Live tracing</h1>

      <FormControl className = "app__dropdown">
        <Select variant = "outlined" onChange={onDropChange} value={country}>
           <MenuItem value = "WW">WorldWide</MenuItem>
          {/* loop through all country and show drop down*/}
          {countries.map((country) => (
            <MenuItem value = {country.value}>{country.name}</MenuItem>
            ))}
        </Select>
      </FormControl>
    </div>

    {/*Header*/}
    {/*Title and select input dropdown field*/}

    {/*info box*/}
    {/*info box*/}
    {/*info box*/}

    {/*table*/}
    {/*graph*/}

    {/*map*/}
    </div>
  );
}
export default App;
