import * as React from 'react';
import { Line } from "react-chartjs-2"
import numeral from "numeral";

const options = {
  legend: {
    display: false,
  },
  elements: {
    point: {
      radius: 0,
    },
  },
  maintainAspectRatio: false,
  tooltips: {
    mode: "index",
    intersect: false,
    callbacks: {
      label: function (tooltipItem, data) {
        return numeral(tooltipItem.value).format("+0,0");
      },
    },
  },
  scales: {
    xAxes: [
      {
        type: "time",
        time: {
          format: "MM/DD/YY",
          tooltipFormat: "ll",
        },
      },
    ],
    yAxes: [
      {
        gridLines: {
          display: false,
        },
        ticks: {
          callback: function (value, index, values) {
            return numeral(value).format("0a");
          },
        },
      },
    ],
  },
};

const buildChart = (data, casesType) => {
  const chartData = [];
  //find the new cases by substracting the difference
  let lastData;
  for(let date in data.cases){
    if(lastData) {
      let newData = {
        x: date,
        y: data[casesType][date] - lastData,
      };
      chartData.push(newData);
    }
    lastData = data[casesType][date];
  }
  return chartData;
};

function LineGraph({casesType}) {
    const [data, setData] = React.useState({});

    React.useEffect(() => {
      const fetchData = async() => {
        await fetch("https://disease.sh/v3/covid-19/historical/all?lastdays=120")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        // get data for plotting line graph
        let chartData = buildChart(data, casesType);
        setData(chartData);
      });
    };
    fetchData();
    }, [casesType]);

    return (
        <div>
            <h1>This is graph</h1>
            {data?.length > 0 && (
            <Line data = {{
              datasets: [{
                blackgroundColor: 'rgba(200,20,50,0.5',
                borderColor: '#CC1034',
                data: data,
              },
              ],
            }}
            options = {options}/>)}            
        </div>
    )
}

export default LineGraph
