import * as React from 'react';
import {
  MenuItem,
  FormControl,
  Select,
  Card,
  CardContent
} from "@material-ui/core";
import InfomationBox from './informationBox';
import Map from './map';
import Table from './Table';
import './App.css';

function App() {
  const [countries, setCountries] = React.useState([]);
  const [country, setCountry] = React.useState(['WW']);
  const [countryInfo, setCountryInfo] = React.useState({});
  const [caseTable, setCaseTable] = React.useState([]);
  React.useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/all")
    .then((response) => response.json())
    .then((data) => {
      setCountryInfo(data);
    });
  }, []);

  React.useEffect(() => {
    //run once as componet loads and not again
    // async -> send requent and wait for it then do something with it
    const getData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => {
        // it give back a big json
        const countries = data.map((country) => (
          { 
            // go through each country
            name : country.country, 
            value: country.countryInfo.iso3
          }
        ));

        setCaseTable(data);
        setCountries(countries);
      });
    };
    getData();
  }, []);

  const onDropChange = async (event) => {
    const countryCode = event.target.value;
    setCountry(countryCode);
    // get info for ww
    // get info for country

    const url = (countryCode === "WW" ? 'https://disease.sh/v3/covid-19/all' :
    `https://disease.sh/v3/covid-19/countries/${countryCode}`
    )
    await fetch(url)
    .then((response) => response.json())
    .then((data) => {
      setCountry(countryCode);
      setCountryInfo(data);

    })
  };
  return (
    <div className="app"> 
    <div className="app_left">
      <div className="app_header">
        <h1>Covid-19 Live tracing</h1>

        <FormControl className = "app_dropdown">
          <Select variant = "outlined" onChange={onDropChange} value={country}>
            <MenuItem value = "WW">WorldWide</MenuItem>
            {/* loop through all country and show drop down*/}
            {countries.map((country) => (
              <MenuItem value = {country.value}>{country.name}</MenuItem>
              ))}
          </Select>
        </FormControl>
      </div>

      <div className="app_stat">
        <InfomationBox title = "Covid-19 Cases" cases = {countryInfo.todayCases} total = {countryInfo.cases}>
        </InfomationBox>
        <InfomationBox title = "Recovered Cases" cases = {countryInfo.todayRecovered} total = {countryInfo.recovered}>
        </InfomationBox>
        <InfomationBox title = "Death Cases" cases = {countryInfo.todayDeaths} total = {countryInfo.deaths}>
        </InfomationBox>
      </div>       
      {/*map*/}
      <Map></Map>
    </div>

     <Card className="app_right">
       <CardContent>
         <h3>Live Cases by Country</h3>
         <Table countries = {caseTable}></Table>
          {/*table*/}
        <h3>Worldwide new cases</h3>
          {/*graph*/}
       </CardContent>
     </Card>




    </div>
  );
}
export default App;
