import * as React from 'react';
import { Line } from "react-chartjs-2"

const buildChart = (data, casesType) => {
  const chartData = [];
  //find the new cases by substracting the difference
  let lastData;
  for(let date in data.cases){
    if(lastData) {
      let newData = {
        x: date,
        y: data[casesType][date] - lastData,
      };
      chartData.push(newData);
    }
    lastData = data[casesType][date];
  }
  return chartData;
};

function LineGraph() {
    const[data, setData] = React.useState({});

    React.useEffect(() => {
      const fetchData = async() => {
        await fetch("https://disease.sh/v3/covid-19/historical/all?lastdays=120")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        // get data for plotting line graph
        let chartData = buildChart(data, casesType);
        setData(chartData);
      });
    };
    fetchData();
    }, [casesType]);

    return (
        <div>
            <h1>This is graph</h1>
            <Line data = {{
              datasets: [{
                blackgroundColor: 'black',
                borderColor: 'black',
                data: data,
              },
              ]
            }}/>            
        </div>
    )
}

export default LineGraph
